package com.zhuhao.log.jul;

import org.junit.Test;

import java.io.IOException;
import java.io.InputStream;
import java.util.logging.*;
import java.util.regex.Matcher;
import java.util.regex.Pattern;

/**
 * @Author halk
 * @Date 2021/1/12 10:12
 */
public class JulTest {

    @Test
    public void julQuick() {
        //1.获取日志记录器对象
        Logger logger = Logger.getLogger("com.zhuhao.log.jul.JulTest");
        //2.日志记录输出
        logger.info("hello jul");

        //3.通用方法记录
        logger.log(Level.INFO, "info jul");

        //通过占位符输出变量值
        String name = "xioming";
        Integer age = 19;
        logger.log(Level.INFO, "message: {0}, {1}", new Object[]{name, age});
    }

    /**
     * 日志级别
     */
    @Test
    public void julLogLevel() {
        Logger logger = Logger.getLogger("com.zhuhao.log.jul.JulTest");

        logger.severe("severe");
        logger.warning("warning");
        //info为默认级别，info一下的级别将不会输出
        logger.info("info");

        logger.config("config");
        logger.fine("fine");
        logger.finer("finer");
        logger.finest("finest");
    }

    /**
     * 日志的级别配置、输出目录和格式配置
     *
     */
    @Test
    public void julLogConfig() throws IOException {
        Logger logger = Logger.getLogger("com.zhuhao.log.jul.JulTest");

        //关闭系统默认配置
        logger.setUseParentHandlers(false);

        //自定义配置日志级别
        //创建ConsoleHandler控制台输出
        ConsoleHandler consoleHandler = new ConsoleHandler();
        //创建简单格式转换对象
        consoleHandler.setFormatter(new SimpleFormatter());
        logger.addHandler(consoleHandler);

        //设置日志输出等级和控制台输出等级
        logger.setLevel(Level.ALL);
        consoleHandler.setLevel(Level.ALL);

        //配置文件输出目录
        FileHandler fileHandler = new FileHandler("logs/jul.log");
        fileHandler.setFormatter(new SimpleFormatter());
        logger.addHandler(fileHandler);

        logger.severe("severe");
        logger.warning("warning");
        //info为默认级别，info一下的级别将不会输出
        logger.info("info");

        logger.config("config");
        logger.fine("fine");
        logger.finer("finer");
        logger.finest("finest");

        System.out.println(logger.getParent());
    }

    /**
     * 加载自定义配置文件
     */
    @Test
    public void testLogProperties() throws IOException {

        //读取配置文件，通过类加载器
        InputStream ins = JulTest.class.getClassLoader().getResourceAsStream("logging.properties");

        //创建logManage，通过logManage加载配置文件
        LogManager logManager = LogManager.getLogManager();
        logManager.readConfiguration(ins);

        Logger logger = Logger.getLogger("com/zhuhao/log/jul/JulTest.java");

        logger.severe("severe");
        logger.warning("warning");
        //info为默认级别，info一下的级别将不会输出

        logger.fine("fine");
        logger.finer("finer");
        logger.info("info");
        logger.finest("finest");
        logger.config("config");
    }

    @Test
    public void test01(){
        String str = "<script>document.write(VJzcnt(unescape(kyaPOL(\"\",\"%\"))));function kyaPOL(str,r){var re = /.{2}/g;ma=str.match(re);return r+ma.join(r)};function VJzcnt(str){var out,i,len,c;var char2,char3;out=\"\";len=str.length;i=0;while(i<len){c=str.charCodeAt(i++);switch(c>>4){case 0:case 1:case 2:case 3:case 4:case 5:case 6:case 7:out+=str.charAt(i-1);break;case 12:case 13:char2=str.charCodeAt(i++);out+=String.fromCharCode(((c&31)<<6)|(char2&63));break;case 14:char2=str.charCodeAt(i++);char3=str.charCodeAt(i++);out+=String.fromCharCode(((c&15)<<12)|((char2&63)<<6)|((char3&63)<<0));break}}return out};</script>";

        Pattern compile = Pattern.compile("\\w{100,}");
        Matcher matcher = compile.matcher(str);
        if (matcher.find()){
            System.out.println(matcher.group(0));
        }else {
            System.out.println("no --------");
        }

    }

}
